import React, {useState} from 'react';
import ExpenseForm from './components/ExpenseForm';
import ExpenseList from './components/ExpenseList';
import TotalExpenses from './components/TotalExpenses';
import './App.css';

function App() {
  const [expenses, setExpenses] = useState([]);
  const [editingId, setEditingId] = useState(null);

  const startEditing = (id) => {
    setEditingId(id);
  };


  const addExpense = (newExpense) => {
    setExpenses((prevExpenses) => [...prevExpenses, newExpense]);
  };


  const deleteExpnese = (id) => {
    setExpenses(expenses.filter(expense => expense.id !== id))
  }

  const editExpense = (id, updatedExpnse) => {
    setExpenses(expenses.map(
      expense => expense.id === id? updatedExpnse : expense))
  }

  const getTotlaExpense = () => {
    return expenses.reduce((total, expense) => total + expense.cost, 0)
  }

  return (
    <div className="App">
      <ExpenseForm onAddExpense={addExpense}/>
      <ExpenseList expenses={expenses} onDeleteExpense={deleteExpnese} startEditing={startEditing} />
      <TotalExpenses expenses={expenses} />
    </div>
  )
}

export default App;
