import React, { useState, useEffect } from 'react';

function ExpenseForm({ onAddExpense, existingExpense, onSaveExpense, onCancel }) {
  const [name, setName] = useState('');
  const [cost, setCost] = useState('');

  // 수정 모드인 경우, 기존 값으로 폼을 초기화합니다.
  useEffect(() => {
    if (existingExpense) {
      setName(existingExpense.name);
      setCost(existingExpense.cost);
    }
  }, [existingExpense]);

  const handleSubmit = (e) => {
    e.preventDefault();

    const expenseData = { name, cost: Number(cost) };

    // 수정 모드인지 확인하고, 적절한 함수를 호출합니다.
    if (existingExpense) {
      onSaveExpense(existingExpense.id, expenseData);
    } else {
      onAddExpense(expenseData);
    }

    // 폼을 초기 상태로 리셋합니다.
    setName('');
    setCost('');
  };

  return (
    <form onSubmit={handleSubmit} className="max-w-sm mx-auto my-4">
      <div className="mb-3">
        <label htmlFor="name" className="form-label">항목 이름</label>
        <input type="text" id="name" value={name} onChange={(e) => setName(e.target.value)} className="input input-bordered w-full" />
      </div>
      <div className="mb-3">
        <label htmlFor="cost" className="form-label">비용</label>
        <input type="number" id="cost" value={cost} onChange={(e) => setCost(e.target.value)} className="input input-bordered w-full" />
      </div>
      <div className="flex justify-between">
        <button type="submit" className="btn btn-primary">
          {existingExpense ? '수정하기' : '추가하기'}
        </button>
        {existingExpense && (
          <button type="button" onClick={onCancel} className="btn btn-ghost">
            취소
          </button>
        )}
      </div>
    </form>
  );
}

export default ExpenseForm;
