import React, {useState} from "react";

function ExpenseForm({onAddExpense}) {
    const [name, setName] = useState('');
    const [cost, setCost] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();

        // 새 지출 항목 객체를 생성합니다.
        const newExpense = {
          id: Date.now(), // 고유 ID 생성
          name: name,
          cost: parseInt(cost, 10) // 비용을 숫자로 변환
        };

        onAddExpense(newExpense); // App 컴포넌트의 addExpense 함수를 호출하여 상태에 추가

        // 폼을 초기화합니다.
        setName('');
        setCost('');
      };

      return (
        <form onSubmit={handleSubmit} className="mb-4">
        <div className="mb-2">
          <label htmlFor="name" className="block text-sm font-bold mb-2">항목 이름</label>
          <input
            type="text"
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          />
        </div>
        <div className="mb-2">
          <label htmlFor="cost" className="block text-sm font-bold mb-2 size color-black">비용</label>
          <input
            type="number"
            id="cost"
            value={cost}
            onChange={(e) => setCost(e.target.value)}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          />
        </div>
        <button type="submit" className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">
          추가
        </button>
      </form>

      );
    }

    export default ExpenseForm;
