import React, {useState} from 'react';
import ExpenseForm from './components/ExpenseForm';

function App() {
  const [expenses, setExpenses] = useState([]);

  const addExpnse = (expenses) => {
    setExpenses([...expenses, expenses]);
  }  

  const deleteExpnese = (id) => {
    setExpenses(expenses.filter(expense => expense.id !== id))
  }

  const editExpense = (id, updatedExpnse) => {
    setExpenses(expenses.map(
      expense => expense.id === id? updatedExpnse : expense))
  }

  const getTotlaExpense = () => {
    return expenses.reduce((total, expense) => total + expense.cost, 0)
  }

  return (
    <div className="App">
      <ExpenseForm onAddExpense={addExpnse}></ExpenseForm>
    </div>
  )
}

export default App;
