import React, {useState} from 'react';
import ExpenseForm from './components/ExpenseForm';
import ExpenseList from './components/ExpenseList';
import TotalExpenses from './components/TotalExpenses';
import './App.css';

function App() {
  const [expenses, setExpenses] = useState([]);
  const [editingId, setEditingId] = useState(null);

  const startEditing = (id) => {
    setEditingId(id);
  };

  const saveExpense = (id, updatedExpense) => {
    setExpenses(expenses.map(expense => expense.id === id ? { ...expense, ...updatedExpense } : expense));
    setEditingId(null);
  };

  const addExpense = (newExpense) => {
    setExpenses((prevExpenses) => [...prevExpenses, newExpense]);
  };


  const deleteExpense = (id) => {
    setExpenses(expenses.filter(expense => expense.id !== id))
  }

  return (
   <div className="container mx-auto mt-8">
      <div className="w-full max-w-2xl mx-auto bg-yellow-100 shadow-md rounded px-8 pt-6 pb-8 mb-4">

      {
        editingId ? (
          <ExpenseForm
            existingExpense={expenses.find(expense => expense.id === editingId)}
            onSaveExpense={saveExpense}
            onCancel={() => setEditingId(null)}
          />
        ) : (
          <ExpenseForm onAddExpense={addExpense} />
        )
      }
      <ExpenseList expenses={expenses} startEditing={startEditing} deleteExpense={deleteExpense} />
      <TotalExpenses expenses={expenses} />
      </div>
    </div>
  );

}

export default App;
