import React from 'react';

function ExpenseList({ expenses, onDeleteExpense }) {
  return (
    <div>
    <h2 className="font-bold mb-4">지출 목록</h2>
    <ul>
      {expenses.map((expense) => (
        <li key={expense.id} className="flex justify-between items-center p-4 bg-white shadow rounded mb-2">
          <div className="flex-1">
            <span className="font-semibold">{expense.name}</span>
          </div>
          <div className="flex-1 text-right">
            <span className="text-gray-500">${expense.cost}</span>
          </div>
          <div>
            <button onClick={() => startEditing(expense.id)} className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-1 px-2 rounded mr-2">수정</button>
            <button onClick={() => onDeleteExpense(expense.id)} className="bg-red-500 hover:bg-red-700 text-white font-bold py-1 px-2 rounded">삭제</button>
          </div>
        </li>
      ))}
    </ul>
    </div>
  );
}

export default ExpenseList;
