import React, { useState } from 'react';

function ExpenseList({ expenses, onSaveExpense, onDeleteExpense }) {
  const [editMode, setEditMode] = useState(null);
  const [editedName, setEditedName] = useState('');
  const [editedCost, setEditedCost] = useState('');

  const handleEdit = (expense) => {
    setEditMode(expense.id);
    setEditedName(expense.name);
    setEditedCost(expense.cost);
  };

  const handleSave = (id) => {
    onSaveExpense(id, { name: editedName, cost: editedCost });
    setEditMode(null);
  };

  const handleCancel = () => {
    setEditMode(null);
  };

  return (
    <ul>
      {expenses.map((expense) => (
        <li key={expense.id} className="flex justify-between items-center p-4">
          {editMode === expense.id ? (
            <div>
              <input
                type="text"
                value={editedName}
                onChange={(e) => setEditedName(e.target.value)}
              />
              <input
                type="text"
                value={editedCost}
                onChange={(e) => setEditedCost(e.target.value)}
              />
            </div>
          ) : (
            <div>
              <span>{expense.name}</span> - <span>${expense.cost}</span>
            </div>
          )}

          <div>
            {editMode === expense.id ? (
              <>
                <button onClick={() => handleSave(expense.id)}>üíæ</button>
                <button onClick={handleCancel}>‚ùå</button>
              </>
            ) : (
              <>
                <button onClick={() => handleEdit(expense)}>‚úèÔ∏è</button>
                <button onClick={() => onDeleteExpense(expense.id)}>üóëÔ∏è</button>
              </>
            )}
          </div>
        </li>
      ))}
    </ul>
  );
}

export default ExpenseList;
